import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const setTokenHeader = (token) => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      delete axios.defaults.headers.common['Authorization'];
    }
  };

  // On mount, try to restore user from token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setTokenHeader(token);
      // validate token by calling /auth/me
      axios.get('http://localhost:3002/auth/me')
        .then(res => {
          setUser(res.data.user);
        })
        .catch(err => {
          console.warn('Invalid token, clearing', err);
          localStorage.removeItem('token');
          setTokenHeader(null);
        })
        .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, []);

  const signup = async ({ name, email, password }) => {
    const res = await axios.post('http://localhost:3002/auth/signup', { name, email, password });
    const { token, user } = res.data;
    localStorage.setItem('token', token);
    setTokenHeader(token);
    setUser(user);
    navigate('/');
  };

  const login = async ({ email, password }) => {
    const res = await axios.post('http://localhost:3002/auth/login', { email, password });
    const { token, user } = res.data;
    localStorage.setItem('token', token);
    setTokenHeader(token);
    setUser(user);
    navigate('/');
  };

  const logout = () => {
    localStorage.removeItem('token');
    setTokenHeader(null);
    setUser(null);
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, signup, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
