import React, { useState } from "react";
import { FaArrowUp, FaArrowDown, FaChartBar } from "react-icons/fa";
import { FiMoreHorizontal } from "react-icons/fi";

const WatchListItem = ({ stock }) => {

  const [show, setShow] = useState(false);

  return (
    <li
      onMouseEnter={() => setShow(true)}
      onMouseLeave={() => setShow(false)}
    >

      <div className="item">

        <span className={stock.isDown ? "down" : "up"}>
          {stock.name}
        </span>

        <div className="itemInfo">

          <span>{stock.percent}</span>

          {stock.isDown ? <FaArrowDown className="down"/> : <FaArrowUp className="up"/>}

          <span>{stock.price}</span>

        </div>

      </div>

      {show && <WatchActions />}

    </li>
  );
};

export default WatchListItem;

/* ================= ACTIONS ================= */

const WatchActions = () => {

  return (
    <div className="actions">

      <Tooltip label="Buy (B)">
        <button className="buy">B</button>
      </Tooltip>

      <Tooltip label="Sell (S)">
        <button className="sell">S</button>
      </Tooltip>

      <Tooltip label="Analytics">
        <button className="icon"><FaChartBar/></button>
      </Tooltip>

      <Tooltip label="More">
        <button className="icon"><FiMoreHorizontal/></button>
      </Tooltip>

    </div>
  );
};

/* ================= TOOLTIP ================= */

const Tooltip = ({ children, label }) => {

  return (
    <div className="tooltip-wrap">
      {children}
      <span className="tooltip">{label}</span>
    </div>
  );
};
