import React from "react";

const WatchList = () => {
  return (
    <div className="watchlist">

      {/* Search */}
      <div className="search-box">
        <input
          placeholder="Search eg: INFY, NIFTY, GOLD MCX"
        />
        <span className="count">9 / 50</span>
      </div>

      {/* Dummy Watchlist */}
      <ul className="watch-items">

        {["INFY", "TCS", "RELIANCE", "NIFTY", "BANKNIFTY"].map((s, i) => (
          <li key={i}>
            <span>{s}</span>
            <span className="price green">+0.42%</span>
          </li>
        ))}

      </ul>

      {/* Styles */}
      <style>{`

      .watchlist{
        width:400px;
        height:100vh;
        background:#fff;
        border-right:1px solid #eee;
        padding:10px;
        font-family:Inter,system-ui;
      }

      .search-box{
        display:flex;
        align-items:center;
        background:#f5f7fa;
        border-radius:6px;
        padding:8px;
        margin-bottom:10px;
      }

      .search-box input{
        border:none;
        outline:none;
        flex:1;
        background:transparent;
        font-size:14px;
      }

      .count{
        font-size:12px;
        color:#777;
      }

      .watch-items{
        list-style:none;
        padding:0;
        margin:0;
        overflow-y:auto;
        height:calc(100vh - 80px);
      }

      .watch-items li{
        display:flex;
        justify-content:space-between;
        padding:10px 6px;
        cursor:pointer;
        border-radius:4px;
        transition:.2s;
      }

      .watch-items li:hover{
        background:#f5f7fa;
      }

      .price{
        font-size:13px;
      }

      .green{color:#2ecc71}
      .red{color:#e74c3c}

      `}</style>

    </div>
  );
};

export default WatchList;
