import React from "react";
import { FaArrowUp, FaArrowDown, FaChartBar } from "react-icons/fa";
import { FiMoreHorizontal } from "react-icons/fi";

const WatchListItem = ({ stock }) => {
  return (
    <li className="watch-item" aria-label={`${stock.name} ${stock.percent}`}>

      <div className="item">

        <span className={`name ${stock.isDown ? "down" : "up"}`}>
          {stock.name}
        </span>

        <div className="itemInfo">

          <span className="percent">{stock.percent}</span>

          {stock.isDown ? (
            <FaArrowDown className="down arrow" aria-hidden />
          ) : (
            <FaArrowUp className="up arrow" aria-hidden />
          )}

          <span className="price">{stock.price}</span>

        </div>

      </div>

      <ActionButtons />

    </li>
  );
};

export default WatchListItem;

/* ================= ACTIONS ================= */
const ActionButtons = () => {
  return (
    <div className="actions" role="group" aria-label="Row actions">

      <Tooltip label="Buy (B)">
        <button className="buy" title="Buy (B)">B</button>
      </Tooltip>

      <Tooltip label="Sell (S)">
        <button className="sell" title="Sell (S)">S</button>
      </Tooltip>

      <Tooltip label="Analytics (A)">
        <button className="icon" title="Analytics"><FaChartBar /></button>
      </Tooltip>

      <Tooltip label="More">
        <button className="icon" title="More"><FiMoreHorizontal /></button>
      </Tooltip>

    </div>
  );
};

/* ================= TOOLTIP ================= */
const Tooltip = ({ children, label }) => {
  return (
    <div className="tooltip-wrap">
      {children}
      <span className="tooltip" role="tooltip">{label}</span>
    </div>
  );
};
