import React, { useState } from "react";
import { FaChartBar, FaTrashAlt } from "react-icons/fa";
import { FiMoreHorizontal } from "react-icons/fi";
import { AiOutlineArrowUp, AiOutlineArrowDown } from "react-icons/ai";

const WatchListItem = ({ stock }) => {
  const [show, setShow] = useState(false);

  return (
    <li
      className="watch-row"
      onMouseEnter={() => setShow(true)}
      onMouseLeave={() => setShow(false)}
    >
      <div className="item">

        <span className={stock.isDown ? "down" : "up"}>
          {stock.name}
        </span>

        <div className="itemInfo">

          <span className={`percent ${stock.isDown ? "down" : "up"}`}>
            {stock.isDown ? <AiOutlineArrowDown /> : <AiOutlineArrowUp />}
            {stock.percent}
          </span>

          <span className="price">{stock.price}</span>

        </div>

      </div>

      {show && (
        <div className="actions">

          <button className="buy-btn">B</button>
          <button className="sell-btn">S</button>

          <button className="icon-btn">
            <FaChartBar />
          </button>

          <button className="icon-btn">
            <FaTrashAlt />
          </button>

          <button className="icon-btn">
            <FiMoreHorizontal />
          </button>

        </div>
      )}
    </li>
  );
};

export default WatchListItem;
