import React from "react";
import { positions } from "../data/data";
import "./Holdings.css"

const Positions = () => {

  // Profit / Loss
  const profitLoss = (p) => ((p.price - p.avg) * p.qty).toFixed(2);

  return (
    <>

      <h3 className="title">Positions ({positions.length})</h3>

      <div className="order-table">

        <table className="table">

          <thead>
            <tr>
              <th>Product</th>
              <th>Instrument</th>
              <th>Qty.</th>
              <th>Avg.</th>
              <th>LTP</th>
              <th>P&L</th>
              <th>Chg.</th>
            </tr>
          </thead>

          <tbody>

            {positions.map((p, i) => {

              const pl = profitLoss(p);

              return (
                <tr key={i}>

                  <td>{p.product}</td>
                  <td><strong>{p.name}</strong></td>
                  <td>{p.qty}</td>
                  <td>{p.avg}</td>
                  <td>{p.price}</td>

                  <td style={{ color: pl >= 0 ? "#2ecc71" : "#e74c3c" }}>
                    {pl}
                  </td>

                  <td style={{ color: p.day.includes("-") ? "#e74c3c" : "#2ecc71" }}>
                    {p.day}
                  </td>

                </tr>
              );
            })}

          </tbody>

        </table>

      </div>

    </>
  );
};

export default Positions;
