import React from "react";
import Menu from "./Menu";

const TopBar = () => {
  return (
    <div className="topbar">

      <div className="indices">

        <div className="index-card">
          <span className="index-name">NIFTY 50</span>
          <span className="index-value green">+100.2</span>
          <span className="index-percent green">(+0.45%)</span>
        </div>

        <div className="index-card">
          <span className="index-name">SENSEX</span>
          <span className="index-value red">-85.6</span>
          <span className="index-percent red">(-0.21%)</span>
        </div>

      </div>

      <Menu />

      {/* Styles */}
      <style>{`

        .topbar{
          height:50px;
          background:#fff;
          border-bottom:1px solid #eee;
          padding:0 25px;
          display:flex;
          justify-content:space-between;
          align-items:center;
          font-family:Inter, system-ui;
        }

        .indices{
          display:flex;
          gap:30px;
        }

        .index-card{
          display:flex;
          align-items:center;
          gap:8px;
          font-size:14px;
        }

        .index-name{
          color:#666;
          font-weight:500;
        }

        .index-value{
          font-weight:600;
        }

        .index-percent{
          font-size:13px;
        }

        .green{
          color:#2ecc71;
        }

        .red{
          color:#e74c3c;
        }

      `}</style>

    </div>
  );
};

export default TopBar;
