import React from "react";
import { Link } from "react-router-dom";

const box = {
  background: "#111",
  borderRadius: 12,
  padding: 20,
  color: "#fff",
  boxShadow: "0 4px 12px rgba(239, 234, 234, 0.4)",
};

const row = {
  display: "grid",
  gridTemplateColumns: "2fr 1fr",
  gap: 20,
  marginTop: 20,
};

const dataRow = {
  display: "flex",
  justifyContent: "space-between",
  margin: "6px 0",
  fontSize: 14,
};

const Funds = () => {
  return (
    <div style={{ padding: 25 }}>

      {/* Top Bar */}
      <div style={{ ...box, display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <p>Instant zero-cost UPI fund transfer</p>

        <div>
          <Link style={btnGreen}>Add Funds</Link>
          <Link style={btnBlue}>Withdraw</Link>
        </div>
      </div>

      <div style={row}>
        {/* Equity */}
        <div style={box}>
          <h3 style={{ marginBottom: 15 }}>Equity Balance</h3>

          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 15 }}>

            <Stat title="Available Margin" value="₹4,043.10" highlight />
            <Stat title="Used Margin" value="₹3,757.30" />
            <Stat title="Available Cash" value="₹4,043.10" />
            <Stat title="Opening Balance" value="₹3,736.40" />

          </div>

          <hr style={{ margin: "15px 0", opacity: 0.2 }} />

          <div style={dataRow}><span>Payin</span><span>4064</span></div>
          <div style={dataRow}><span>SPAN</span><span>0</span></div>
          <div style={dataRow}><span>Delivery Margin</span><span>0</span></div>
          <div style={dataRow}><span>Exposure</span><span>0</span></div>
          <div style={dataRow}><span>Options Premium</span><span>0</span></div>

          <hr style={{ margin: "15px 0", opacity: 0.2 }} />

          <div style={dataRow}><span>Total Collateral</span><span>0</span></div>
        </div>

        {/* Commodity */}
        <div style={{ ...box, textAlign: "center" }}>
          <h3>Commodity</h3>

          <p style={{ opacity: 0.7, marginTop: 20 }}>
            You don’t have a commodity account
          </p>

          <Link style={{ ...btnBlue, display: "inline-block", marginTop: 20 }}>
            Open Account
          </Link>
        </div>
      </div>
    </div>
  );
};

/* Small Card Component */
const Stat = ({ title, value, highlight }) => (
  <div style={{
    background: "#f7f7f7",
    padding: 15,
    borderRadius: 10
  }}>
    <p style={{ fontSize: 12, opacity: .6 }}>{title}</p>
    <h3 style={{ color: highlight ? "#00ff90" : "#fff" }}>{value}</h3>
  </div>
);

/* Buttons */
const btnGreen = {
  background: "#00c853",
  padding: "8px 18px",
  borderRadius: 8,
  color: "#fcfafa",
  marginRight: 10,
  textDecoration: "none",
  fontWeight: 600
};

const btnBlue = {
  background: "#2979ff",
  padding: "8px 18px",
  borderRadius: 8,
  color: "#fff",
  textDecoration: "none",
  fontWeight: 600
};

export default Funds;
