import React,{useState , useEffect} from "react";
import {axios} from "axios";
import "./Holdings.css";


const Holdings = () => {

  const [holdings, setHoldings] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3002/allholdings")
      .then((res) => res.json())
      .then((data) => setHoldings(data))
      .catch((err) => console.error("Error fetching holdings:", err));
  }, []);

  const currentValue = (h) => (h.price * h.qty).toFixed(2);
  const profitLoss = (h) => ((h.price - h.avg) * h.qty).toFixed(2);

  return (
    <div className="holdings-wrapper">

      <h3 className="title fade-in">Holdings ({holdings.length})</h3>

      <div className="order-table slide-up">

        <table className="table responsive-table">

          <thead>
            <tr>
              <th>Instrument</th>
              <th>Qty.</th>
              <th>Avg.</th>
              <th>LTP</th>
              <th>Cur. val</th>
              <th>P&L</th>
              <th>Net chg.</th>
              <th>Day chg.</th>
            </tr>
          </thead>

          <tbody>
            {holdings.map((h, i) => {

              const pl = profitLoss(h);

              return (
                <tr key={i} className="row-animate">

                  <td data-label="Instrument">
                    <strong>{h.name}</strong>
                  </td>

                  <td data-label="Qty">{h.qty}</td>
                  <td data-label="Avg">{h.avg}</td>
                  <td data-label="LTP">{h.price}</td>
                  <td data-label="Value">{currentValue(h)}</td>

                  <td
                    data-label="P&L"
                    className={pl >= 0 ? "profit" : "loss"}
                  >
                    {pl}
                  </td>

                  <td
                    data-label="Net"
                    className={h.net.includes("-") ? "loss" : "profit"}
                  >
                    {h.net}
                  </td>

                  <td
                    data-label="Day"
                    className={h.day.includes("-") ? "loss" : "profit"}
                  >
                    {h.day}
                  </td>

                </tr>
              );
            })}
          </tbody>

        </table>

      </div>

    </div>
  );
};

export default Holdings;
