import React from "react";
import { holdings } from "../data/data";

const Holdings = () => {

  // Current value
  const currentValue = (h) => (h.price * h.qty).toFixed(2);

  // Profit / Loss
  const profitLoss = (h) => ((h.price - h.avg) * h.qty).toFixed(2);

  return (
    <>

      <h3 className="title">Holdings ({holdings.length})</h3>

      <div className="order-table">

        <table className="table">

          <thead>
            <tr>
              <th>Instrument</th>
              <th>Qty.</th>
              <th>Avg.</th>
              <th>LTP</th>
              <th>Cur. val</th>
              <th>P&L</th>
              <th>Net chg.</th>
              <th>Day chg.</th>
            </tr>
          </thead>

          <tbody>
            {holdings.map((h, i) => {

              const pl = profitLoss(h);

              return (
                <tr key={i}>

                  <td>
                    <strong>{h.name}</strong>
                  </td>

                  <td>{h.qty}</td>
                  <td>{h.avg}</td>
                  <td>{h.price}</td>
                  <td>{currentValue(h)}</td>

                  <td style={{ color: pl >= 0 ? "#2ecc71" : "#e74c3c" }}>
                    {pl}
                  </td>

                  <td style={{ color: h.net.includes("-") ? "#e74c3c" : "#2ecc71" }}>
                    {h.net}
                  </td>

                  <td style={{ color: h.day.includes("-") ? "#e74c3c" : "#2ecc71" }}>
                    {h.day}
                  </td>

                </tr>
              );
            })}
          </tbody>

        </table>

      </div>

    </>
  );
};

export default Holdings;
