import React from "react";
import { watchlist } from "../data/data";
import WatchListItem from "./WatchListItem";
import "./WatchList.css";

const WatchList = ({onClose}) => {
  return (
    <div className="watchlist-container">

      <div className="search-container">
        <div className="search">
          <svg className="search-icon" width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M21 21l-4.35-4.35" stroke="#6b7280" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
            <circle cx="11" cy="11" r="6" stroke="#6b7280" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          <input
            placeholder="Search e.g. INFY, NIFTY, GOLD"
          />
        </div>
        <span className="count">{watchlist.length} / 50</span>

        {/* mobile close */}
        {onClose && (
          <button className="drawer-close" onClick={onClose} aria-label="Close watchlist">âœ•</button>
        )}
      </div>

      <ul>
        {watchlist.map((stock, i) => (
          <WatchListItem stock={stock} key={i} />
        ))}
      </ul>

    </div>
  );
};

export default WatchList;
